mapper.select:
SELECT a.BBS_ID, a.BBS_TY_CODE, b.CODE_NM as BBS_TY_CODE_NM, a.BBS_ATTRB_CODE, c.CODE_NM as BBS_ATTRB_CODE_NM, a.BBS_NM, a.TMPLAT_ID, a.USE_AT, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM FROM LETTNBBSMASTER a LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM004' AND USE_AT='Y') b ON a.BBS_TY_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM009' AND USE_AT='Y') c ON a.BBS_ATTRB_CODE = c.CODE WHERE 1=1
AND a.USE_AT = #{useAt}
AND a.BBS_NM LIKE '%' || #{searchWrd} || '%'
AND b.CODE_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT a.BBS_ID, a.BBS_TY_CODE, b.CODE_NM as BBS_TY_CODE_NM, a.BBS_ATTRB_CODE, c.CODE_NM as BBS_ATTRB_CODE_NM, a.BBS_NM, a.TMPLAT_ID, a.USE_AT, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM FROM LETTNBBSMASTER a LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM004' AND USE_AT='Y') b ON a.BBS_TY_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM009' AND USE_AT='Y') c ON a.BBS_ATTRB_CODE = c.CODE, LETTNBBSUSE d WHERE a.BBS_ID = d.BBS_ID AND d.TRGET_ID = #{trgetId}
AND a.BBS_NM LIKE '%' || #{searchWrd} || '%'
AND b.CODE_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.BBS_ID) FROM LETTNBBSMASTER a LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM004' AND USE_AT='Y') b ON a.BBS_TY_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM009' AND USE_AT='Y') c ON a.BBS_ATTRB_CODE = c.CODE, LETTNBBSUSE d WHERE a.BBS_ID = d.BBS_ID AND d.TRGET_ID = #{trgetId}
AND a.BBS_NM LIKE '%' || #{searchWrd} || '%'
AND b.CODE_NM LIKE '%' || #{searchWrd} || '%'
