mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.NTT_ID, a.NTT_SJ, a.FRST_REGISTER_ID, NVL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.NTT_SJ LIKE '%' || #{searchWrd} || '%'
AND a.NTT_CN LIKE '%' || #{searchWrd} || '%'
AND b.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC ) TB ) Z WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


SELECT NVL(MAX(RDCNT),0)+1 AS RDCNT FROM LETTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}


SELECT
	NVL(MAX(NTT_NO),0)+1 AS NTT_NO
FROM
	LETTNBBS	
WHERE
	BBS_ID = #{bbsId}
AND
	SORT_ORDR = #{sortOrdr}
AND 
	NTT_ID = #{nttId}

mapper.update:

UPDATE LETTNBBS SET 
	RDCNT = #{inqireCo},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATETIME
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}


UPDATE LETTNBBS SET 
	NTT_NO = #{nttNo}
WHERE NTT_ID = #{nttId}


UPDATE LETTNBBS
SET USE_AT = 'N'
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.select:

SELECT
	PASSWORD
FROM
	LETTNBBS	
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.update:

UPDATE LETTNBBS SET 
	NTT_NO = NTT_NO + 1
WHERE BBS_ID = #{bbsId} AND SORT_ORDR = #{sortOrdr}
AND NTT_NO > #{nttNo}


UPDATE LETTNBBS SET 
	NTT_NO = #{nttNo}
WHERE BBS_ID = #{bbsId} 
AND NTT_ID = #{nttId}
