mapper.select:
SELECT a.NTT_ID, a.NTT_SJ, a.FRST_REGISTER_ID, COALESCE(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE '%' || #{searchWrd} || '%'
AND a.NTT_CN LIKE '%' || #{searchWrd} || '%'
AND b.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.NTT_ID) FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE '%' || #{searchWrd} || '%'
AND a.NTT_CN LIKE '%' || #{searchWrd} || '%'
AND b.USER_NM LIKE '%' || #{searchWrd} || '%'


SELECT COALESCE(MAX(RDCNT),0)+1 AS RDCNT FROM LETTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}::integer
