UPDATE LETTNBBSUSE SET 
	USE_AT = 'N',
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = NOW()::timestamp(0)
WHERE BBS_ID = #{bbsId}
AND TRGET_ID = #{trgetId}

mapper.select:
SELECT a.BBS_ID, e.BBS_NM, a.TRGET_ID, a.REGIST_SE_CODE, b.CODE_NM as REGIST_SE_CODE_NM, a.USE_AT, a.FRST_REGISTER_ID, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM, '시스템 활용' CMMNTY_ID, '시스템 활용' CMMNTY_NM, '시스템 활용' CLB_ID, '시스템 활용' CLB_NM, '시스템 활용' BBS_TY_CODE FROM LETTNBBSUSE a LEFT OUTER JOIN LETTNBBSMASTER e ON a.BBS_ID = e.BBS_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE b ON b.CODE = a.REGIST_SE_CODE WHERE b.CODE_ID = 'COM001' AND a.REGIST_SE_CODE != 'REGC07'
AND e.BBS_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT * FROM ( SELECT a.BBS_ID, b.BBS_NM, a.TRGET_ID, d.USER_NM, a.REGIST_SE_CODE, c.CODE_NM as REGIST_SE_CODE_NM, a.USE_AT, a.FRST_REGISTER_ID, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM FROM LETTNBBSUSE a LEFT OUTER JOIN LETTNBBSMASTER b ON a.BBS_ID = b.BBS_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE c ON c.CODE = a.REGIST_SE_CODE LEFT OUTER JOIN COMVNUSERMASTER d ON a.TRGET_ID = d.ESNTL_ID WHERE a.BBS_ID IN ( SELECT BBS_ID FROM LETTNBBSUSE WHERE TRGET_ID = #{trgetId} AND USE_AT ='Y' ) AND a.REGIST_SE_CODE = 'REGC07' UNION ALL SELECT a.BBS_ID, b.BBS_NM, a.TRGET_ID, '[전체]' USER_NM, a.REGIST_SE_CODE, c.CODE_NM as REGIST_SE_CODE_NM, a.USE_AT, a.FRST_REGISTER_ID, to_char(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM FROM LETTNBBSUSE a LEFT OUTER JOIN LETTNBBSMASTER b ON a.BBS_ID = b.BBS_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE c ON c.CODE = a.REGIST_SE_CODE WHERE a.BBS_ID IN ( SELECT BBS_ID FROM LETTNBBSUSE WHERE TRGET_ID = #{trgetId} ) AND a.REGIST_SE_CODE in ('REGC05', 'REGC06') UNION ALL SELECT a.BBS_ID, b.BBS_NM, case WHEN e.EMPLYR_ID is null THEN f.EMPLYR_ID ELSE e.EMPLYR_ID END as TRGET_ID, case WHEN g.USER_NM is null THEN h.USER_NM ELSE g.USER_NM END as USER_NM, 'REGC07' as REGIST_SE_CODE, '게시판사용자등록' as REGIST_SE_CODE_NM, '' as USE_AT, '' as FRST_REGISTER_ID, '' as FRST_REGIST_PNTTM FROM LETTNBBSUSE a LEFT OUTER JOIN LETTNBBSMASTER b ON a.BBS_ID = b.BBS_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE c ON c.CODE = a.REGIST_SE_CODE LEFT OUTER JOIN LETTNCMMNTYUSER e ON e.CMMNTY_ID = #{trgetId} LEFT OUTER JOIN LETTNCLUBUSER f ON f.CLB_ID = #{trgetId} LEFT OUTER JOIN COMVNUSERMASTER g ON e.EMPLYR_ID = g.ESNTL_ID LEFT OUTER JOIN COMVNUSERMASTER h ON f.EMPLYR_ID = h.ESNTL_ID WHERE a.BBS_ID IN ( SELECT BBS_ID FROM LETTNBBSUSE WHERE TRGET_ID = #{trgetId} AND USE_AT ='Y' ) AND a.REGIST_SE_CODE in ('REGC05', 'REGC06') AND ( (a.BBS_ID, e.EMPLYR_ID) not in (SELECT BBS_ID, TRGET_ID FROM LETTNBBSUSE) OR (a.BBS_ID, f.EMPLYR_ID) not in (SELECT BBS_ID, TRGET_ID FROM LETTNBBSUSE) ) ) tt
WHERE tt.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY tt.USER_NM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

