mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID, USER_ID, USER_NM, USER_ZIP, USER_ADRES, USER_EMAIL FROM COMVNUSERMASTER WHERE 1=1
AND USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.ESNTL_ID, a.USER_ID, a.USER_NM, a.USER_ZIP, a.USER_ADRES, a.USER_EMAIL, b.USE_AT, (SELECT USE_AT from LETTNCMMNTY WHERE CMMNTY_ID = #{trgetId}) AS DELETED_AT FROM COMVNUSERMASTER a, LETTNCMMNTYUSER b WHERE a.ESNTL_ID = b.EMPLYR_ID AND b.CMMNTY_ID = #{trgetId}
AND a.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.ESNTL_ID, a.USER_ID, a.USER_NM, a.USER_ZIP, a.USER_ADRES, a.USER_EMAIL, b.USE_AT, (SELECT USE_AT from LETTNCMMNTY WHERE CMMNTY_ID = #{trgetId}) AS DELETED_AT FROM COMVNUSERMASTER a, LETTNCMMNTYUSER b WHERE a.ESNTL_ID = b.EMPLYR_ID AND b.MNGR_AT = 'Y' AND b.CMMNTY_ID = #{trgetId}
AND a.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.ESNTL_ID, a.USER_ID, a.USER_NM, a.USER_ZIP, a.USER_ADRES, a.USER_EMAIL, b.USE_AT, (SELECT USE_AT from LETTNCLUB WHERE CLB_ID = #{trgetId}) AS DELETED_AT FROM COMVNUSERMASTER a, LETTNCLUBUSER b WHERE a.ESNTL_ID = b.EMPLYR_ID AND b.CLB_ID = #{trgetId}
AND a.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.ESNTL_ID, a.USER_ID, a.USER_NM, a.USER_ZIP, a.USER_ADRES, a.USER_EMAIL, b.USE_AT, (SELECT USE_AT from LETTNCLUB WHERE CLB_ID = #{trgetId}) AS DELETED_AT FROM COMVNUSERMASTER a, LETTNCLUBUSER b WHERE a.ESNTL_ID = b.EMPLYR_ID AND b.OPRTR_AT = 'Y' AND b.CLB_ID = #{trgetId}
AND a.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY a.USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


SELECT EMPLYR_ID as ESNTL_ID, CLB_ID as TRGET_ID 
FROM LETTNCLUBUSER WHERE USE_AT = 'Y'
AND CLB_ID = #{trgetId}


SELECT EMPLYR_ID as ESNTL_ID, CMMNTY_ID as TRGET_ID 
FROM LETTNCMMNTYUSER WHERE USE_AT = 'Y'
AND CMMNTY_ID =  #{trgetId}
