mapper.select:
SELECT A.SCHDUL_ID, A.SCHDUL_SE, A.SCHDUL_DEPT_ID, A.SCHDUL_KND_CODE, DATE_FORMAT(A.SCHDUL_BEGINDE, '%Y%m%d%H%i')SCHDUL_BGNDE, DATE_FORMAT(A.SCHDUL_ENDDE, '%Y%m%d%H%i')SCHDUL_ENDDE, A.SCHDUL_NM, A.SCHDUL_CN, A.SCHDUL_PLACE, A.SCHDUL_IPCR_CODE, A.SCHDUL_CHARGER_ID, A.ATCH_FILE_ID, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.REPTIT_SE_CODE FROM LETTNSCHDULINFO A WHERE 1=1
AND SCHDUL_SE LIKE CONCAT(CONCAT('%', #{searchKeyword}), '%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
