mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.SCHDUL_ID, A.SCHDUL_SE, A.SCHDUL_DEPT_ID, A.SCHDUL_KND_CODE, A.SCHDUL_BEGINDE AS "schdulBgnde", A.SCHDUL_ENDDE, A.SCHDUL_NM, A.SCHDUL_CN, A.SCHDUL_PLACE, A.SCHDUL_IPCR_CODE, A.SCHDUL_CHARGER_ID, A.ATCH_FILE_ID, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.REPTIT_SE_CODE FROM LETTNSCHDULINFO A WHERE 1=1
AND SCHDUL_SE LIKE CONCAT(CONCAT('%', #{searchKeyword}), '%')
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
